let createServer,readFile,resolve;_5cb‍.w("http",[["createServer",["createServer"],function(v){createServer=v}]]);_5cb‍.w("fs",[["readFile",["readFile"],function(v){readFile=v}]]);_5cb‍.w("path",[["resolve",["resolve"],function(v){resolve=v}]]);



const server = createServer((request, response) => {
    switch (request.url) {

        case '/status': {
            response.writeHead(200, {
                'Content-Type': 'application/json',
            });
            response.write(JSON.stringify({
                status: 'Okay',
            }));
            response.end();
            break;
        }

        case '/sign-in':{
            const filePath = resolve(__dirname, './pages/login/sign-in.html');
            readFile(filePath, (error, file) =>{
                if(error){
                    response.writeHead(500, 'Canot process HTML file.');
                    response.end();
                    return;
                }

                response.writeHead(200);
                response.write(file);
                response.end();
            });
            break;
        }

        case '/style.css':{
             const filePath = resolve(__dirname, './pages/login/sign-in.css');
            readFile(filePath);
            break;
        }

        case '/authenticate':{
            let data = '';
            request.on('data', (chunk) =>{
                data+=chunk;
            });
            request.on('end',()=>{

                response.writeHead(301, {
                    Location:'/home',
                });

                response.end();
            } );
            break;
        }

        case '/home':{
            const path = resolve(__dirname, './pages/home/home.html');
            readFile(path, (error, file) =>{
                if(error){
                    response.writeHead(500, 'Canot process HTML file.');
                    response.end();
                    return;
                }

                response.writeHead(200);
                response.write(file);
                response.end();
            });
            break;
        }

        default: {
            response.writeHead(404, 'Service not found');
            response.end();
            break;
        }
    }
});

const PORT = process.env.PORT ? parseInt(process.env.PORT) : 8000;
const HOSTNAME = process.env.HOSTNAME || '127.0.0.1';

server.listen(PORT, HOSTNAME, () => {
    console.log(`server is listening at http://${HOSTNAME}:${PORT}`)
});